import styled from "styled-components";
import React, {useState, useEffect} from 'react';
import {ethers} from 'ethers';
// import Image from "next/image";
let provider = new ethers.providers.Web3Provider(window.ethereum);
let signer = provider.getSigner();

export default function Project1 () {

  // Read Data from smart contract 

  // 1. Contract address

  const contractAddress = "0xd9145CCE52D386f254917e481eB44e9943F39138";

  // 2. Contract ABI
  const abi = [
	{
		"inputs": [
			{
				"internalType": "string",
				"name": "campaignTitle",
				"type": "string"
			},
			{
				"internalType": "uint256",
				"name": "requiredCampaignAmount",
				"type": "uint256"
			},
			{
				"internalType": "string",
				"name": "imgURI",
				"type": "string"
			},
			{
				"internalType": "string",
				"name": "storyURI",
				"type": "string"
			},
			{
				"internalType": "address",
				"name": "campaignOwner",
				"type": "address"
			}
		],
		"stateMutability": "nonpayable",
		"type": "constructor"
	},
	{
		"anonymous": false,
		"inputs": [
			{
				"indexed": true,
				"internalType": "address",
				"name": "donar",
				"type": "address"
			},
			{
				"indexed": true,
				"internalType": "uint256",
				"name": "amount",
				"type": "uint256"
			},
			{
				"indexed": true,
				"internalType": "uint256",
				"name": "timestamp",
				"type": "uint256"
			}
		],
		"name": "donated",
		"type": "event"
	},
	{
		"inputs": [],
		"name": "donate",
		"outputs": [],
		"stateMutability": "payable",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "image",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "owner",
		"outputs": [
			{
				"internalType": "address payable",
				"name": "",
				"type": "address"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "receivedAmount",
		"outputs": [
			{
				"internalType": "uint256",
				"name": "",
				"type": "uint256"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "requiredAmount",
		"outputs": [
			{
				"internalType": "uint256",
				"name": "",
				"type": "uint256"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "story",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "title",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	}
]
  // 3. Provider

  const [amount, setAmount] = useState();
   const [change, setChange] = useState(false);
    const [mydonations, setMydonations] = useState([]);





  const DonateFunds = async () => {
    try {
      await window.ethereum.request({ method: 'eth_requestAccounts' });
      // const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();

      const contract = new ethers.Contract(contractAddress, abi, signer);

      const transaction = await contract.donate({value: ethers.utils.parseEther(amount)});
      await transaction.wait();

      setChange(true);
      setAmount('');

  } catch (error) {
      console.log(error);
  }

  }

  return (
    <DetailWrapper>
      <LeftContainer>
        <ImageSection>
          <img layout="fill" src="https://mir-s3-cdn-cf.behance.net/projects/404/6ac9b691738305.Y3JvcCw5MDIsNzA2LDM4OCwyNDM.jpg"/>
        </ImageSection>
        <Text>
          Imagine a place where blockchain projects can attract holders, receive investments, or find partners starting from zero. TruePNL proudly announces the launch of the native launchpad platform to make it all come true.
TruePNL introduces a platform to connect hand-picked, groundbreaking blockchain projects and crypto investors, delivering a convenient, transparent, and secure mechanism for raising capital, investing and distributing tokens. 

The platform allows cryptocurrency projects to raise funds by creating token exchange pools and launching public or private sales rounds using smart contracts on the Ethereum and Binance Smart Chain networks.
        </Text>
      </LeftContainer>
      <RightContainer>
        <Title>Project 1</Title>
          <DonateSection>
            <Input value={amount} onChange={(e) => setAmount(e.target.value)} type="number" placeholder="Enter Amount:" />
            <Donate onClick={DonateFunds}>Donate</Donate>
          </DonateSection>
		  <CollectSection>
			<Input value={amount} onChange={(e) => setAmount(e.target.value)} type="number" placeholder="Enter Amount:" />
			<Collect>Collect</Collect>
		  </CollectSection>
          <FundsData>
            <Funds>
              <FundText>Target Amount</FundText>
              <FundText>10 ETH</FundText>
            </Funds>
            <Funds>
              <FundText>Funds Raised</FundText>
              <FundText>5 ETH</FundText>
            </Funds>
			<Funds>
              <FundText>Target Percentage Return</FundText>
              <FundText>30%</FundText>
            </Funds>
			<Funds>
              <FundText>Percentage Ownership</FundText>
              <FundText>20%</FundText>
            </Funds>
          </FundsData>
          <Donated>
            <LiveDonation>
              <DonationTitle>Recent Donation</DonationTitle>
              <Donation>
                <DonationData>0xf7233e2a224...Y31</DonationData>
                <DonationData>1.5 ETH</DonationData>
                <DonationData>6/03/2021, 9:11:03 AM</DonationData>
              </Donation>
            </LiveDonation>
          </Donated>
      </RightContainer>
    </DetailWrapper>
  );
}

const DetailWrapper = styled.div`
display: flex;
justify-content: space-between;
padding: 20px;
width: 98%;
background-color:#0E0B16 ;
// background-color: #A2396A;
// background-color: #4717F6;
// background-color: #E7DFDD;
`;

const LeftContainer = styled.div`
width: 45%;
`;

const RightContainer = styled.div`
width: 50%;
`;

const ImageSection = styled.div`
width: 100%;
position: relative;
height: 350px;
margin-left: 50px;
`;

const Text = styled.p`
font-family: "Roboto";
font-size: large;
color: #E7DFDD;
text-align: justify;
`;
const Title = styled.h1`
padding: 0;
margin: 0;
font-family: "Poppins";
font-size: x-large;
color: #E7DFDD;
`;

const DonateSection = styled.div`
display: flex;
width: 100%;
justify-content: space-between;
align-items: center;
margin-top: 10px;
`;

const CollectSection = styled.div`
display: flex;
width: 100%;
justify-content: space-between;
align-items: center;
