import styled from "styled-components";
import React, { useState, useEffect } from "react";
import "@fontsource/sora"
import { ethers } from "ethers";
// import Image from "next/image";
let provider = new ethers.providers.Web3Provider(window.ethereum);
let signer = provider.getSigner();

export default function Project1() {
	// Read Data from smart contract

	// 1. Contract address

	const contractAddress = "0x3c725134d74D5c45B4E4ABd2e5e2a109b5541288";

	// 2. Contract ABI
	const abi = [
	{
		"inputs": [
			{
				"internalType": "string",
				"name": "campaignTitle",
				"type": "string"
			},
			{
				"internalType": "uint256",
				"name": "requiredCampaignAmount",
				"type": "uint256"
			},
			{
				"internalType": "string",
				"name": "imgURI",
				"type": "string"
			},
			{
				"internalType": "string",
				"name": "storyURI",
				"type": "string"
			},
			{
				"internalType": "address",
				"name": "campaignOwner",
				"type": "address"
			}
		],
		"stateMutability": "nonpayable",
		"type": "constructor"
	},
	{
		"anonymous": false,
		"inputs": [
			{
				"indexed": true,
				"internalType": "address",
				"name": "donar",
				"type": "address"
			},
			{
				"indexed": true,
				"internalType": "uint256",
				"name": "amount",
				"type": "uint256"
			},
			{
				"indexed": true,
				"internalType": "uint256",
				"name": "timestamp",
				"type": "uint256"
			}
		],
		"name": "donated",
		"type": "event"
	},
	{
		"inputs": [],
		"name": "donate",
		"outputs": [],
		"stateMutability": "payable",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "image",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "owner",
		"outputs": [
			{
				"internalType": "address payable",
				"name": "",
				"type": "address"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "receivedAmount",
		"outputs": [
			{
				"internalType": "uint256",
				"name": "",
				"type": "uint256"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "requiredAmount",
		"outputs": [
			{
				"internalType": "uint256",
				"name": "",
				"type": "uint256"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "story",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	},
	{
		"inputs": [],
		"name": "title",
		"outputs": [
			{
				"internalType": "string",
				"name": "",
				"type": "string"
			}
		],
		"stateMutability": "view",
		"type": "function"
	}
]
	// 3. Provider

	const [inAmount, setinAmount] = useState();
	const [change, setChange] = useState(false);
	const [mydonations, setMydonations] = useState([]);
	const [outAmount, setoutAmount] = useState();
	const [inA, setA] = useState();


	const DonateFunds = async () => {
		try {
			await window.ethereum.request({ method: "eth_requestAccounts" });
			// const provider = new ethers.providers.Web3Provider(window.ethereum);
			const signer = provider.getSigner();

			const contract = new ethers.Contract(contractAddress, abi, signer);

			const transaction = await contract.donate({
				value: ethers.utils.parseEther(inAmount)

			});
			await transaction.wait();
			// console.log(transaction.value);

			setChange(true);
			setinAmount("");
			setA(true);

			// const info =  web3.eth.getTransactionReceipt(tx.result);           
			// console.log(info);
		} catch (error) {
			console.log(error);
		}
	};

	return (
		<DetailWrapper>
			<LeftContainer>
				<ImageSection>
